   SUBROUTINE RRTMLWRAD(rthraten,glw,emiss                        &
                       ,p8w,p3d,pi3d                              &
                       ,dz8w,t3d,t8w,rho3d,r,g                    &
                       ,icloud, warm_rain                         &
                       ,ids,ide, jds,jde, kds,kde                 & 
                       ,ims,ime, jms,jme, kms,kme                 &
                       ,its,ite, jts,jte, kts,kte                 &
                       ,qv3d,qc3d,qr3d                            &
                       ,qi3d,qs3d,qg3d,cldfra3d                   &
                       ,f_qv,f_qc,f_qr,f_qi,f_qs,f_qg             &
                                                                  )
!------------------------------------------------------------------
   IMPLICIT NONE
!------------------------------------------------------------------
   LOGICAL, INTENT(IN )      ::        warm_rain
!
   INTEGER, INTENT(IN )      ::        ids,ide, jds,jde, kds,kde, &
                                       ims,ime, jms,jme, kms,kme, &
                                       its,ite, jts,jte, kts,kte

   INTEGER, INTENT(IN )      ::        ICLOUD
!
   REAL, DIMENSION( ims:ime, kms:kme, jms:jme )                 , &
         INTENT(IN   ) ::                                   dz8w, &
                                                             T3D, &
                                                             t8w, &
                                                             p8w, &
                                                             P3D, &
                                                            pi3D, &
                                                           rho3D
!
   REAL, DIMENSION( ims:ime, kms:kme, jms:jme )                 , &
         INTENT(INOUT)  ::                              RTHRATEN
!
   REAL, DIMENSION( ims:ime, jms:jme )                          , &
         INTENT(IN   )  ::                                 EMISS
!
   REAL, DIMENSION( ims:ime, jms:jme )                          , &
         INTENT(INOUT)  ::                                   GLW
!
   REAL, INTENT(IN  )   ::                                   R,G
!
! Optional
!
   REAL, DIMENSION( ims:ime, kms:kme, jms:jme )                 , &
         OPTIONAL                                               , &
         INTENT(IN   ) ::                                         &
                                                        CLDFRA3D, &
                                                            QV3D, &
                                                            QC3D, &
                                                            QR3D, &
                                                            QI3D, &
                                                            QS3D, &
                                                            QG3D

   LOGICAL, OPTIONAL, INTENT(IN )      ::        F_QV,F_QC,F_QR,F_QI,F_QS,F_QG

!  LOCAL VARS
 
   REAL, DIMENSION( kts:kte+1 ) ::                          Pw1D, &
                                                            Tw1D, &
                                                            PHYD

   REAL, DIMENSION( kts:kte ) ::                          TTEN1D, &
                                                        CLDFRA1D, &
                                                            DZ1D, &
                                                             P1D, &
                                                         PHYDMID, &
                                                             T1D, &
                                                            QV1D, &
                                                            QC1D, &
                                                            QR1D, &
                                                            QI1D, &
                                                            QS1D, &
                                                            QG1D
!
    REAL   ::                                   TSFC,GLW0,EMISS0,FP
!
    INTEGER:: i,j,K,NK
    LOGICAL :: predicate

!------------------------------------------------------------------
      write(0,*)' enter RRTM'

!-----CALCULATE LONG WAVE RADIATION
!                                                              
   j_loop: DO J=jts,jte
   i_loop: DO I=its,ite

! reverse vars 
! p1D pw1D are in mb

! NEED HYDROSTATIC PRESSURE HERE (MONOTONIC CHANGE WITH HEIGHT)
! PHYD REPLACES P8W, PHYDMID REPLACES P3D
         PHYD(kts) = p8w(I,kts,J)
! first guess
         DO K = KTS,KTE
            PHYD(K+1) = PHYD(K) - G*RHO3D(I,K,J)*DZ8W(I,K,J)
         ENDDO
! correction factor FP to match p8w(I,kts,J)-p8w(I,kte,J)
         FP = (p8w(I,kts,J)-p8w(I,kte,J))/(PHYD(KTS)-PHYD(KTE))
! final pass
         DO K = KTS,KTE
            PHYD(K+1) = PHYD(K) - G*RHO3D(I,K,J)*DZ8W(I,K,J)*FP
            PHYDMID(K)= 0.5*(PHYD(K)+PHYD(K+1))
         ENDDO

         do k=kts,kte+1
            NK=kme-k+kms
!           Pw1D(K) = p8w(I,NK,J)/100.
            Pw1D(K) = PHYD(NK)/100.
            Tw1D(K) = t8w(I,NK,J)
         enddo

         DO K=kts,kte
            QV1D(K)=0.
            QC1D(K)=0.
            QR1D(K)=0.
            QI1D(K)=0.
            QS1D(K)=0.
            CLDFRA1D(k)=0.
         ENDDO

         DO K=kts,kte
            NK=kme-1-K+kms
            QV1D(K)=QV3D(I,NK,J)
            QV1D(K)=max(0.,QV1D(K))
         ENDDO

         DO K=kts,kte
            NK=kme-1-K+kms
            TTEN1D(K)=0.
            T1D(K)=T3D(I,NK,J)
!           P1D(K)=P3D(I,NK,J)/100.
            P1D(K)=PHYDMID(NK)/100.
            DZ1D(K)=dz8w(I,NK,J)
         ENDDO

         IF (ICLOUD .ne. 0) THEN
            IF ( PRESENT( CLDFRA3D ) ) THEN
              DO K=kts,kte
                 NK=kme-1-K+kms
                 CLDFRA1D(k)=CLDFRA3D(I,NK,J)
              ENDDO
            ENDIF

            IF (PRESENT(F_QC) .AND. PRESENT(QC3D)) THEN
              IF ( F_QC) THEN
                 DO K=kts,kte
                    NK=kme-1-K+kms
                    QC1D(K)=QC3D(I,NK,J)
                    QC1D(K)=max(0.,QC1D(K))
                 ENDDO
              ENDIF
            ENDIF

            IF (PRESENT(F_QR) .AND. PRESENT(QR3D)) THEN
              IF ( F_QR) THEN
                 DO K=kts,kte
                    NK=kme-1-K+kms
                    QR1D(K)=QR3D(I,NK,J)
                    QR1D(K)=max(0.,QR1D(K))
                 ENDDO
              ENDIF
            ENDIF

! This logic is tortured because cannot test F_QI unless
! it is present, and order of evaluation of expressions
! is not specified in Fortran

            IF ( PRESENT ( F_QI ) ) THEN
              predicate = F_QI
            ELSE
              predicate = .FALSE.
            ENDIF

            IF (.NOT. predicate .and. .not. warm_rain) THEN
               DO K=kts,kte
                  IF (T1D(K) .lt. 273.15) THEN
                  QI1D(K)=QC1D(K)
                  QS1D(K)=QR1D(K)
                  QC1D(K)=0.
                  QR1D(K)=0.
                  ENDIF
               ENDDO
            ENDIF

            IF (PRESENT(F_QI) .AND. PRESENT(QI3D)) THEN
               DO K=kts,kte
                  NK=kme-1-K+kms
                  QI1D(K)=QI3D(I,NK,J)
                  QI1D(K)=max(0.,QI1D(K))
               ENDDO
            ENDIF

            IF (PRESENT(F_QS) .AND. PRESENT(QS3D)) THEN
               IF (F_QS) THEN
                  DO K=kts,kte
                     NK=kme-1-K+kms
                     QS1D(K)=QS3D(I,NK,J)
                     QS1D(K)=max(0.,QS1D(K))
                  ENDDO
               ENDIF
            ENDIF

            IF (PRESENT(F_QG) .AND. PRESENT(QG3D)) THEN
               IF (F_QG) THEN
                  DO K=kts,kte
                     NK=kme-1-K+kms
                     QG1D(K)=QG3D(I,NK,J)
                     QG1D(K)=max(0.,QG1D(K))
                  ENDDO
               ENDIF
            ENDIF

         ENDIF

         EMISS0=EMISS(I,J)
         GLW0=0. 
         TSFC=Tw1D(kme)

         CALL RRTM(tten1d,glw0,tsfc,cldfra1d,t1d,tw1d,qv1d,qc1d,   &
                   qr1d,qi1d,qs1d,qg1d,p1d,pW1d,dz1d,              &
                   emiss0,r,g,                                     &
                   kts,kte                                         )
 
         GLW(I,J)=GLW0

         DO K=kts,kte
            nk=kme-1-k+kms
            rthraten(i,k,j)=rthraten(i,k,j)+tten1d(nk)/pi3d(i,k,j)
         ENDDO

      END DO i_loop
   END DO j_loop                                           

!-------------------------------------------------------------------

   END SUBROUTINE RRTMLWRAD


