!     BAND 8:  1080-1180 cm-1 (low (i.e.>~300mb) - H2O; high - O3)               
                                                                                 
      INTEGER, PARAMETER :: NGS7=88                                       
                                                                                 
      INTEGER, INTENT(IN )                      :: kts,ktep1

      INTEGER, INTENT(IN )                      :: LAYSWTCH

      REAL, DIMENSION( NGPT,kts:ktep1 ),                    &
            INTENT(INOUT)        ::                  PFRAC, &
